import java.util.*;public class BOJ_10819 {	static int max = 0;	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int N = sc.nextInt();		int num[] = new int[N];		for(int i = 0; i < N; i++) {			num[i] = sc.nextInt();		}		perm(num, 0, N);		System.out.println(max);	}	static void perm(int[] a, int depth, int n) {		if(depth==n) {			sum(a, n); return;		} for (int i=depth; i<n; i++) {			swap(a, i, depth); perm(a, depth+1, n); swap(a, i, depth);		}	}	static void swap(int[] a, int depth, int n) {		int temp = a[depth]; a[depth] = a[n]; a[n] = temp;	}	static void sum(int[] a, int k) {		int sum =0;		for (int i = 2; i <= k; i++) {			sum += Math.abs(a[i-2]-a[i-1]);		}		if(max<sum) { max = sum; }	}}